const { stripIndents, oneLine } = require('common-tags');
const Command = require('../base');
const { disambiguation } = require('../../util');

module.exports = class HelpCommand extends Command {
	constructor(client) {
		super(client, {
			name: 'help',
			group: 'util',
			memberName: 'help',
			aliases: ['h'],
			description: 'Affiche une liste des commandes disponibles ou des informations détaillées sur une commande spécifiée.',
			details: oneLine`
			La commande peut faire partie d'un nom de commande ou d'un nom de commande entier.
			S'il n'est pas spécifié, toutes les commandes disponibles seront listées.
			`,
			examples: ['help', 'help prefix'],
			guarded: true,

			args: [
				{
					key: 'command',
					prompt: "Pour quelle commande souhaitez-vous afficher l'aide ?",
					type: 'string',
					default: ''
				}
			]
		});
	}

	async run(msg, args) { // eslint-disable-line complexity
		msg.delete();
		const groups = this.client.registry.groups;
		const commands = this.client.registry.findCommands(args.command, false, msg);
		const showAll = args.command && args.command.toLowerCase() === 'all';
		if(args.command && !showAll) {
			if(commands.length === 1) {
				let help = stripIndents`
					${oneLine`
						__Command **${commands[0].name}**:__ ${commands[0].description}
						${commands[0].guildOnly ? ' (Usable only in servers)' : ''}
						${commands[0].nsfw ? ' (NSFW)' : ''}
					`}

					**Format:** ${msg.anyUsage(`${commands[0].name}${commands[0].format ? ` ${commands[0].format}` : ''}`)}
				`;
				if(commands[0].aliases.length > 0) help += `\n**Aliases:** ${commands[0].aliases.join(', ')}`;
				help += `\n${oneLine`
					**Group:** ${commands[0].group.name}
					(\`${commands[0].groupID}:${commands[0].memberName}\`)
				`}`;
				if(commands[0].details) help += `\n**Details:** ${commands[0].details}`;
				if(commands[0].examples) help += `\n**Examples:**\n${commands[0].examples.join('\n')}`;

				const messages = [];
				try {
					messages.push(await msg.direct(help));
					if(msg.channel.type !== 'dm') messages.push(await msg.reply('Je vous ai envoyé un MP avec des informations.'));
				} catch(err) {
					messages.push(await msg.reply("Impossible de vous envoyer le MP d'aide. Vous avez probablement désactivé les MP."));
				}
				return messages;
			} else if(commands.length > 15) {
				return msg.reply('Plusieurs commandes trouvées. Veuillez être plus précis.');
			} else if(commands.length > 1) {
				return msg.reply(disambiguation(commands, 'commands'));
			} else {
				return msg.reply(
					`Impossible d'identifier la commande. Utilisation${msg.usage(
						null, msg.channel.type === 'dm' ? null : undefined, msg.channel.type === 'dm' ? null : undefined
					)} pour afficher la liste de toutes les commandes.`
				);
			}
		} else {
			const messages = [];
			try {
				messages.push(await msg.direct(stripIndents`
					${oneLine`
						Pour exécuter une commande dans ${msg.guild ? msg.guild.name : "n'importe quel serveur"},
						utiliser ${Command.usage('command', msg.guild ? msg.guild.commandPrefix : null, this.client.user)}.
						Par exemple, ${Command.usage('prefix', msg.guild ? msg.guild.commandPrefix : null, this.client.user)}.
					`}
					Pour exécuter une commande dans ce MP, utilisez simplement${Command.usage('command', null, null)} sans préfixe.

					Utiliser ${this.usage('<command>', null, null)} pour afficher des informations détaillées sur une commande spécifique.
					Utiliser ${this.usage('all', null, null)} pour afficher une liste de *toutes* les commandes, pas seulement celles disponibles.

					__**${showAll ? 'Toutes les commandes' : `Commandes disponibles dans ${msg.guild || 'ce MP'}`}**__

					${groups.filter(grp => grp.commands.some(cmd => !cmd.hidden && (showAll || cmd.isUsable(msg))))
						.map(grp => stripIndents`
							__${grp.name}__
							${grp.commands.filter(cmd => !cmd.hidden && (showAll || cmd.isUsable(msg)))
								.map(cmd => `**${cmd.name}:** ${cmd.description}${cmd.nsfw ? ' (NSFW)' : ''}`).join('\n')
							}
						`).join('\n\n')
					}
				`, { split: true }));
				if(msg.channel.type !== 'dm') messages.push(await msg.reply('Je vous ai envoyé un MP avec des informations.'));
			} catch(err) {
				messages.push(await msg.reply("Impossible de vous envoyer le MP d'aide. Vous avez probablement désactivé les MP."));
			}
			return messages;
		}
	}
};
